import React, { useState } from 'react';
import database from './firebase';
import { Link } from 'react-router-dom'
import { getDatabase, ref, set, addDoc } from "firebase/database";

function Prompt01() {
  const [name , setName] = useState();
  const [age , setAge] = useState();
      
  // Push Function
  function writeUserData( name, age,) {
    const db = getDatabase();
    addDoc(ref(db,'prompt1'), {
      name: name,
      age: age,
      
    });
  }
 


  return (
    <div className="App" style={{marginTop : 250}}>
      <center>
      <input placeholder="Enter your name" value={name} 
      onChange={(e) => setName(e.target.value)}/>
      <br/><br/>
      <input placeholder="Enter your age" value={age} 
      onChange={(e) => setAge(e.target.value)}/>
      <br/><br/> 
     
      <Link to="/home">
      <button onClick={writeUserData}>PUSH</button>
      </Link>
      </center>
    </div>
  );
}

export default Prompt01;
