          placeholder="Name"
          value={prompt1}
          onChange={(e) => setPrompt1(e.target.value)}
        />
        <Link to="home"> 
        <button onClick={submit}>Submit</button>
        </Link>
      </div>
    </div>
  );
}
  
export default Prompt1;
