import React, { useState } from 'react';
import {db} from './firebase01';
 


  




function Prompt01() {
  const [name , setName] = useState();
  const [age , setAge] = useState();
      
  // Push Function
  const Push = () => {
    db.ref("prompt1").set({
      name : name,
      age : age,
    }).catch(alert);
  }
 


  return (
    <div className="App" style={{marginTop : 250}}>

      <form onSubmit={Push}>
        <input type= "text"  />
        <button type = "submit">Save</button>
      </form>
    </div>
 


    
  
  );
}

export default Prompt01;
